// Three.js r128 minimal implementation for development
// This is a simplified version for development purposes only

const THREE = {}; // Will be filled by the real three.js when available

// If we're in development, try to use a CDN fallback
if (typeof document !== 'undefined') {
  const script = document.createElement('script');
  script.src = 'https://unpkg.com/three@0.128.0/build/three.min.js';
  script.onerror = function() {
    console.log('Could not load Three.js from CDN, using fallback...');
  };
  document.head.appendChild(script);
}

window.THREE = THREE;
